mapper.insert:
    
    	SELECT
    		CONCAT('mileage_use_', LPAD(IFNULL(MAX(SUBSTR(mileage_use_code, 13)), 0) + 1, 2, '0'))
    	FROM mileage_use
    
		INSERT INTO mileage_use
		(   mileage_use_code
		   ,student_id
		   ,mileage_use_reason_code
		   ,mileag_deduct_date
		   ,mileag_deduct
		   ,mileage_anticipate_del_date
		   ,mileage_use_group_code
		   ,mileage_group_code
		   ,mileage_memo
		   ,mileage_wait_division
		) VALUES (
			#{mileageUseCode}
		   ,#{studentId}
		   ,#{mileageUseReasonCode}
		   ,CASE
		   		WHEN #{mileageWaitDivision} = 'A'
		   		THEN NOW()
		   		ELSE NULL
		   	END
		   ,#{mileagDeduct}
		   ,CASE
		   		WHEN #{mileageWaitDivision} = 'A'
		   		THEN DATE_ADD(NOW(), INTERVAL 3 YEAR)
		   		ELSE NULL
		   	END
		   ,#{mileageUseGroupCode}
		   ,#{mileageGroupCode}
		   ,#{mileageMemo}
		   ,#{mileageWaitDivision}
		);
